$(document).ready(function () {
  var swiper = new Swiper(".mySwiper", {
    effect: "fade",
    speed: 500,
    pagination: {
      el: ".swiper-pagination",
      clickable: true,
    },
    navigation: {
      nextEl: ".swiper-button-next",
      prevEl: ".swiper-button-prev",
    },
    autoplay: {
      delay: 5000, // Thời gian trễ giữa các slide (đơn vị: ms)
    },
    loop: "true",
  });

  swiper.on("slideChange", function () {
    // Lấy index của slide hiện tại
    var currentIndex = swiper.realIndex;
    ele = document.querySelector(".cover-title h3");
    ele.classList.remove("t-1");
    ele.classList.remove("t-2");
    ele.classList.remove("t-3");
    ele.classList.remove("t-4");
    ele.classList.add(`t-${currentIndex + 1}`);
    console.log(currentIndex);
  });

  var swiper2 = new Swiper(".mySwiper-2", {
    effect: "fade",
    speed: 500,
    pagination: {
      el: ".swiper-pagination-2",
      clickable: true,
    },
    navigation: {
      nextEl: ".swiper-button-next-2",
      prevEl: ".swiper-button-prev-2",
    },
    // autoplay: {
    //   delay: 5000, // Thời gian trễ giữa các slide (đơn vị: ms)
    // },
    loop: "true",
  });
});

